import React, { useState } from 'react'
import { Link } from 'react-router-dom';


///////////////////////IMPORT DOS COMPONENTES + CSS//////////////////////////////
import "./senhaRecover.css"
//////////////////////////////////////////////////////////////////////////

function RecoverSenha(){
    const [usuario, setUsuario] = useState("")
    const [erroUsuario, setErroUsuario] = useState("")
    const [erroSenha, setErroSenha] = useState("")
    const [erroLogin, setErroLoin] = useState("")

    const RecSenha = async () => {
        console.log("chamando a função")
        console.log(usuario)
        console.log(senha)
        

        if(!usuario.trim()){
            setErroUsuario("Por favor insira o nome de usuário")
        }

        if(!senha.trim()){
            setErroSenha("Por favor insira sua senha")
            return
        }

        if (!usuario.trim() || !senha.trim()) {
            return;
        }
    };
      

    return(
        <>
                    <div className='rec.senha'>
                        {erroLogin && <label className='erro' onChange={ () => {if(erroUsuario) setErroUsuario(""); if (erroSenha) setErroSenha("")}}>{erroLogin}</label>}  
                        <h6>Digite seu email</h6>
                        <input type='email' 
                            id='usuario' 
                            placeholder='Digite seu Email' 
                            value={email} 
                        >
                        </input>
                            <button id='recover' >LOGIN</button>
                        
                    </div>
        </>
    )
}

export default RecoverSenha
