import React from "react";
import { Link } from 'react-router-dom'; //import para usar rotas
      

function Rodape(){
    let popup = document.getElementById("popup");

        function openPopup() {
            popup.classList.add("open-popup");
        }

        function closePopup() {
            popup.classList.remove("open-popup");
        }
    return(
        <>
            <div class="container">
                <button type="submit" class="btn" onclick="openPopup()">Submit</button>
                <div class="popup" id="popup">
                    <h2>Hello</h2>
                    <p>Lorem, ipsum dolor sit amet consectetur adipisicing elit. Hic qui voluptatum dolorem nesciunt est quae sit provident magni accusamus voluptatibus!</p>
                    <button class="close-btn" onclick="closePopup()">Ok</button>
                </div>
            </div>
        </>
    )
}

export default recuperarSenha
