import React, { useState } from 'react';

function App() {
  const [formSelecionado, setFormSelecionado] = useState(null);

  const handleSelecionarFormulario = (formId) => {
    setFormSelecionado(formId);
  };

  const renderFormulario1 = () => (
    <form className="space-y-2">
      <h2>Tipos de Acomodações</h2>
      <input type="text" placeholder="Nome" />
      <input type="email" placeholder="Email" />
      <input type="tel" placeholder="Telefone" />
      <input type="text" placeholder="Endereço" />
      <input type="text" placeholder="Cidade" />
    </form>
  );

  const renderFormulario2 = () => (
    <form className="space-y-2">
      <h2>Quartos</h2>
      <input type="text" placeholder="Produto" />
      <input type="number" placeholder="Quantidade" />
      <input type="text" placeholder="Categoria" />
      <input type="text" placeholder="Fornecedor" />
      <input type="text" placeholder="Observações" />
    </form>
  );

  return (
    <div style={{ padding: '20px' }}>
      <h1>Sistema de acomodações</h1> <br></br>
      <input type="button" value="Input 1" onClick={() => handleSelecionarFormulario(1)} />
      <input type="button" value="Input 2" onClick={() => handleSelecionarFormulario(2)} />

      <div style={{ marginTop: '20px' }}>
        {formSelecionado === 1 && renderFormulario1()}
        {formSelecionado === 2 && renderFormulario2()}
      </div>
    </div>
  );
}

export default App;
