import React, { useState } from 'react';

function RecoverPage() {
  const [email, setEmail] = useState('');
  const [mensagem, setMensagem] = useState('');
  const [carregando, setCarregando] = useState(false);

  const verificarEmail = async () => {
    setCarregando(true);
    setMensagem('');

    try {
      const resposta = await fetch('http://localhost:8080/recover_senha', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email }),
      });

      const dados = await resposta.json();
      if (dados.existe) {
        setMensagem('✅ Email encontrado no sistema!');
      } else {
        setMensagem('❌ Email não encontrado.');
      }
    } catch (error) {
      setMensagem('Erro ao verificar email.');
    }

    setCarregando(false);
  };

  return (
    <div style={styles.container}>
      <h2>Verificar Email</h2>
      <input
        type="email"
        placeholder="Digite o email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        style={styles.input}
      />
      <button onClick={verificarEmail} style={styles.button} disabled={carregando}>
        {carregando ? 'Verificando...' : 'Verificar'}
      </button>
      <p>{mensagem}</p>
    </div>
  );
}

const styles = {
  container: {
    maxWidth: 400,
    margin: '50px auto',
    padding: 20,
    border: '1px solid #ddd',
    borderRadius: 10,
    textAlign: 'center',
    fontFamily: 'Arial',
  },
  input: {
    width: '100%',
    padding: 10,
    fontSize: 16,
    marginBottom: 10,
    borderRadius: 5,
    border: '1px solid #ccc',
  },
  button: {
    padding: '10px 20px',
    fontSize: 16,
    borderRadius: 5,
    backgroundColor: '#007bff',
    color: 'white',
    border: 'none',
    cursor: 'pointer',
  },
};

export default RecoverPage;
