import React from 'react'
import { Link } from 'react-router-dom'; //import para usar rotas
import { useEffect, useState } from 'react' //import para recuperar dados do back
import { API_URL } from '../../url';
import conteudo_1 from '../../assets/conteudo_1.jpg'
import conteudo_2 from '../../assets/conteudo_2.jpg'
import conteudo_3 from '../../assets/conteudo_3.jpg'
import fachada from '../../assets/fachada.jpg'
import entradaImg from '../../assets/entrada_hotel.jpg'
///////////////////////IMPORT DOS COMPONENTES + CSS//////////////////////////////
import "./home.css"
import HeaderComponente from '../../componetes/header/headerComponente';
//////////////////////////////////////////////////////////////////////////


function HomePage() {
  // //busca pelos dados no back precisa estar dentro da função//
  // const [dados, setDados] = useState([]); //define dados como um array vazio setdados é usado para atualizar o valor de dados, substituir dados pelo item da consulta
  // //useeffect é a chamada da api onde iremos bucar os dados na rota definida no backedn
  // useEffect(() => {
  //   fetch(`${API_URL}/sua-rota`) //definição da rota da api
  //   .then((res) => res.json())
  //   .then((data) => setDados(data)) //uso do set para atribuir valor a variavel
  //   .catch((err) => console.error('Erro ao buscar dados:', err)); //mensagem de erro
  // }, []);

    return (
      <>
        return (
  <>
    <HeaderComponente />
    <main className="page" style={{ backgroundImage: `url(${entradaImg})` }}/>
      <div className="overlay"></div>
      <div>
        <div className="corpo_central">
          <h1>Hospede-se na melhor rede de hotéis do mundo!</h1>
          <h4>Nossos hotéis contam com uma ampla variedade de diversões e entretenimento para você e sua família!</h4>
        </div>
        <hr />
        <div className="conteudo1_hotel">
          <img src={conteudo_1} id="conteudo1_img" alt="logo" />
          <h3>Seja para momentos de descanso ou para atividades divertidas...</h3>
        </div>
        <hr />
        <div className="conteudo2_hotel">
          <img src={conteudo_2} id="conteudo2" alt="logo" />
          <h3>Piscinas: Relaxe nas nossas amplas piscinas...</h3>
        </div>
        <hr />
        <div className="conteudo3_hotel">
          <img src={conteudo_3} id="conteudo3" alt="logo" />
          <h3>Cercado por jardins exuberantes, trilhas sombreadas...</h3>
        </div>
        <hr />
        <div className="sobre">
          <img src={fachada} id="fachada_img" alt="logo" />
          <h3>Cercado por jardins exuberantes, trilhas sombreadas...</h3>
        </div>
      </div>
    <div>
      {/* Conteúdo adicional aqui */}
      {/* {dados.map((dados) => (
        <li key={dados.id}>{dados.nome}</li>
      ))} */}
    </div>
  </>
);

        <div>
          {/* Aqui você pode colocar mais conteúdo depois
          {dados.map((dados) => ( //coamndo igual o ngfor do angular percorre o json por meio de chave falor e exibe na tela, usar nome da variavel que foi atribuido o valor
          <li key={dados.id}>{dados.nome}</li>))}  */}
        </div>
      
      </>

    );
  }
  
  export default HomePage;
