import "./cadCargo.css"
import {UserCog2 } from 'lucide-react';
import { useEffect, useState } from 'react' //import para recuperar dados do back
import { API_URL } from '../../url';
import { useForm} from 'react-hook-form';
import { z } from 'zod';
import { zodResolver } from '@hookform/resolvers/zod';


function CadCargo (){


    const validar = z.object({
        cargo : z.refine(cargo => cargo.trim().length > 0, { message: "Número é obrigatório"})
    })

    const cadastrarCargo = async(dados) =>{
        console.log(JSON.stringify(dados))


    }

    const {
        register,
        formState: { errors }
    } = useForm({
        resolver: zodResolver(validar)
    })









    return(
        <>
            <div className="alinhar-cadCargo-content">
                <label className="edit-carCargo-label">
                    <UserCog2 size={18}/>
                    Nome do cargo
                </label>
                <input type="text" placeholder="Insira o nome para o novo cargo" className="cadCargo-input" {...register("cargo")}></input>
                <button onClick={cadastrarCargo}>Cadastrar</button>
            </div>
        </>
    )
}

export default CadCargo
