import { Link } from 'react-router-dom'; //import para usar rotas
import { useEffect, useState } from 'react' //import para recuperar dados do back
import { API_URL } from '../api' //import da rota da api
/////////////////////////////////
/////////////Teste de import////////////////
import React from 'react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { z } from 'zod';
import { User, Phone, MapPin, Calendar, Flag, Home, Mail, IdCard, FileText, Lock } from 'lucide-react';
import { useToast } from '@/components/ui/use-toast';
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Card } from '@/components/ui/card';
///////////////////////////////////////////

function CadastroPage(){
    const formSchema = z.object({
        fullName: z.string().min(3, { message: 'Nome completo é obrigatório' }),
        birthday: z.string().min(1, { message: 'Data de nascimento é obrigatória' }),
        fatherName: z.string().min(3, { message: 'Nome do pai é obrigatório' }),
        motherName: z.string().min(3, { message: 'Nome da mãe é obrigatório' }),
        nationality: z.enum(['brasileira', 'estrangeira']),
        documentType: z.enum(['cpf', 'rg', 'passport']),
        documentNumber: z.string().min(1, { message: 'Número do documento é obrigatório' }),
        phone: z.string().min(1, { message: 'Telefone é obrigatório' }),
        email: z.string().email({ message: 'Email inválido' }),
        password: z.string().min(6, { message: 'A senha deve ter pelo menos 6 caracteres' }),
        address: z.string().min(1, { message: 'Endereço é obrigatório' }),
        city: z.string().min(1, { message: 'Cidade é obrigatória' }),
        state: z.string().min(1, { message: 'Estado é obrigatório' }),
        country: z.string().min(1, { message: 'País é obrigatório' }),
      });
      
      
      const GuestRegistrationForm = () => {
        const { toast } = useToast();
        const [nationality, setNationality] = useState<'brasileira' | 'estrangeira'>('brasileira');
        
        const { register, handleSubmit, formState: { errors }, setValue, watch } = useForm({
          resolver: zodResolver(formSchema),
          defaultValues: {
            nationality: 'brasileira',
            documentType: 'cpf',
            country: 'Brasil',
          }
        });
      
        const currentNationality = watch('nationality');
        const documentType = watch('documentType');
      
        const onSubmit = () => {
          console.log('Dados do formulário:');
          toast({
            title: "Registro realizado com sucesso!",
            description: `Bem-vindo ao Hotel Universe, !`,
          });
        };
      
        const handleNationalityChange = () => {
          setNationality(value);
          setValue('nationality', value);
          
          if (value === 'brasileira') {
            setValue('documentType', 'cpf');
            setValue('country', 'Brasil');
          } else {
            setValue('documentType', 'passport');
            setValue('country', '');
          }
        };

    return(
        <form onSubmit={handleSubmit(onSubmit)} className="space-y-6">
        <Card className="form-section">
          <h2 className="form-section-title">Informações Pessoais</h2>
          
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="space-y-2">
              <Label htmlFor="fullName" className="flex items-center gap-2">
                <User size={18} />
                Nome Completo
              </Label>
              <Input
                id="fullName"
                placeholder="Digite seu nome completo"
                {...register('fullName')}
              />
              {errors.fullName && (
                <p className="text-sm text-destructive">{errors.fullName.message}</p>
              )}
            </div>
  
            <div className="space-y-2">
              <Label htmlFor="birthday" className="flex items-center gap-2">
                <Calendar size={18} />
                Data de Nascimento
              </Label>
              <Input
                id="birthday"
                type="date"
                {...register('birthday')}
              />
              {errors.birthday && (
                <p className="text-sm text-destructive">{errors.birthday.message}</p>
              )}
            </div>
          
            <div className="space-y-2">
              <Label htmlFor="fatherName" className="flex items-center gap-2">
                <User size={18} />
                Nome do Pai
              </Label>
              <Input
                id="fatherName"
                placeholder="Nome completo do pai"
                {...register('fatherName')}
              />
              {errors.fatherName && (
                <p className="text-sm text-destructive">{errors.fatherName.message}</p>
              )}
            </div>
          
            <div className="space-y-2">
              <Label htmlFor="motherName" className="flex items-center gap-2">
                <User size={18} />
                Nome da Mãe
              </Label>
              <Input
                id="motherName"
                placeholder="Nome completo da mãe"
                {...register('motherName')}
              />
              {errors.motherName && (
                <p className="text-sm text-destructive">{errors.motherName.message}</p>
              )}
            </div>
  
            <div className="space-y-2">
              <Label htmlFor="email" className="flex items-center gap-2">
                <Mail size={18} />
                Email
              </Label>
              <Input
                id="email"
                type="email"
                placeholder="Digite seu email"
                {...register('email')}
              />
              {errors.email && (
                <p className="text-sm text-destructive">{errors.email.message}</p>
              )}
            </div>
  
            <div className="space-y-2">
              <Label htmlFor="password" className="flex items-center gap-2">
                <Lock size={18} />
                Senha
              </Label>
              <Input
                id="password"
                type="password"
                placeholder="Digite sua senha"
                {...register('password')}
              />
              {errors.password && (
                <p className="text-sm text-destructive">{errors.password.message}</p>
              )}
            </div>
          </div>
        </Card>
  
        <Card className="form-section">
          <h2 className="form-section-title">Nacionalidade e Documentação</h2>
          
          <div className="space-y-4">
            <div>
              <Label className="flex items-center gap-2 mb-2">
                <Flag size={18} />
                Nacionalidade
              </Label>
              <RadioGroup 
                defaultValue="brasileira" 
                className="flex flex-col space-y-1"
                value={nationality}
                onValueChange={() => handleNationalityChange(value)}
              >
                <div className="flex items-center space-x-2">
                  <RadioGroupItem value="brasileira" id="brasileira" />
                  <Label htmlFor="brasileira">Brasileira</Label>
                </div>
                <div className="flex items-center space-x-2">
                  <RadioGroupItem value="estrangeira" id="estrangeira" />
                  <Label htmlFor="estrangeira">Estrangeira</Label>
                </div>
              </RadioGroup>
            </div>
  
            {nationality === 'brasileira' ? (
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div className="space-y-2">
                  <Label className="flex items-center gap-2 mb-2">
                    <IdCard size={18} />
                    Tipo de Documento
                  </Label>
                  <RadioGroup 
                    defaultValue="cpf" 
                    className="flex flex-col space-y-1"
                    value={documentType}
                    onValueChange={() => setValue('documentType', value)}
                  >
                    <div className="flex items-center space-x-2">
                      <RadioGroupItem value="cpf" id="cpf" />
                      <Label htmlFor="cpf">CPF</Label>
                    </div>
                    <div className="flex items-center space-x-2">
                      <RadioGroupItem value="rg" id="rg" />
                      <Label htmlFor="rg">RG</Label>
                    </div>
                  </RadioGroup>
                </div>
  
                <div className="space-y-2">
                  <Label htmlFor="documentNumber" className="flex items-center gap-2">
                    <IdCard size={18} />
                    {documentType === 'cpf' ? 'Número do CPF' : 'Número do RG'}
                  </Label>
                  <Input
                    id="documentNumber"
                    placeholder={documentType === 'cpf' ? "Digite seu CPF" : "Digite seu RG"}
                    {...register('documentNumber')}
                  />
                  {errors.documentNumber && (
                    <p className="text-sm text-destructive">{errors.documentNumber.message}</p>
                  )}
                </div>
              </div>
            ) : (
              <div className="space-y-2">
                <Label htmlFor="documentNumber" className="flex items-center gap-2">
                  <FileText size={18} />
                  Número do Passaporte
                </Label>
                <Input
                  id="documentNumber"
                  placeholder="Digite seu número de passaporte"
                  {...register('documentNumber')}
                />
                {errors.documentNumber && (
                  <p className="text-sm text-destructive">{errors.documentNumber.message}</p>
                )}
              </div>
            )}
          </div>
        </Card>
  
        <Card className="form-section">
          <h2 className="form-section-title">Contato e Endereço</h2>
          
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="space-y-2">
              <Label htmlFor="phone" className="flex items-center gap-2">
                <Phone size={18} />
                Telefone
              </Label>
              <Input
                id="phone"
                placeholder="Digite seu telefone"
                {...register('phone')}
              />
              {errors.phone && (
                <p className="text-sm text-destructive">{errors.phone.message}</p>
              )}
            </div>
  
            <div className="space-y-2">
              <Label htmlFor="email" className="flex items-center gap-2">
                <Mail size={18} />
                Email
              </Label>
              <Input
                id="email"
                type="email"
                placeholder="Digite seu email"
                {...register('email')}
              />
              {errors.email && (
                <p className="text-sm text-destructive">{errors.email.message}</p>
              )}
            </div>
            
            <div className="space-y-2">
              <Label htmlFor="address" className="flex items-center gap-2">
                <Home size={18} />
                Endereço
              </Label>
              <Input
                id="address"
                placeholder="Rua, número, complemento"
                {...register('address')}
              />
              {errors.address && (
                <p className="text-sm text-destructive">{errors.address.message}</p>
              )}
            </div>
  
            <div className="space-y-2">
              <Label htmlFor="city" className="flex items-center gap-2">
                <MapPin size={18} />
                Cidade
              </Label>
              <Input
                id="city"
                placeholder="Digite sua cidade"
                {...register('city')}
              />
              {errors.city && (
                <p className="text-sm text-destructive">{errors.city.message}</p>
              )}
            </div>
  
            <div className="space-y-2">
              <Label htmlFor="state" className="flex items-center gap-2">
                <MapPin size={18} />
                Estado
              </Label>
              <Input
                id="state"
                placeholder="Digite seu estado"
                {...register('state')}
              />
              {errors.state && (
                <p className="text-sm text-destructive">{errors.state.message}</p>
              )}
            </div>
  
            <div className="space-y-2">
              <Label htmlFor="country" className="flex items-center gap-2">
                <Flag size={18} />
                País
              </Label>
              <Input
                id="country"
                placeholder="Digite seu país"
                {...register('country')}
              />
              {errors.country && (
                <p className="text-sm text-destructive">{errors.country.message}</p>
              )}
            </div>
          </div>
        </Card>
  
        <div className="flex justify-center pt-6">
          <Button type="submit" className="w-full max-w-md bg-primary hover:bg-primary/90 text-white">
            Finalizar Registro
          </Button>
        </div>
      </form>
    )
}
}

export default CadastroPage
