import React from 'react'
import { Link } from 'react-router-dom'; //import para usar rotas
import { useEffect, useState } from 'react' //import para recuperar dados do back
import { API_URL } from '../../url';
import HeaderComponente from '../../componetes/header/headerComponente';
import { User, Phone, MapPin, Calendar, Flag, Home, Mail, IdCard, FileText, Lock, MapPinHouse, Building2, Landmark, Building, Map } from 'lucide-react';
import "./cadastro.css"
import { useForm } from 'react-hook-form';
import { z } from 'zod';
import { zodResolver } from '@hookform/resolvers/zod';
import { data } from '@remix-run/router';




function CadastroPage(){
    const [nacionalidade, setNacionalidade] = useState("brasileiro")
    const [documento, setDocumento] = useState("CPF")
    const [cep, setCep] = useState("")
    const [dadosEndereco, setDadosEndereco] = useState("")
    const [erroCep, setErroCep] = useState("")
    const [cpf_rg, setCPF_RG] = useState("")
    const [senha, setSenha] = useState("")
    


    const verificarNacionalidade = (event) => {
        const novaNacionalidade = event.target.value;
        setNacionalidade(novaNacionalidade);
    };

    const selecionarDocumento = (doc) => {
        const novoDocumento = doc.target.value
        setDocumento(novoDocumento)
        
    }

    ///////////////////API CORREIOS/////////////////////

    const buscarCep = async () => {
        console.log("chamando função");
        console.log("CEP digitado:", cep);
      
        try {
          const resposta = await fetch(`${API_URL}/api/cep/${cep}`);
          const data = await resposta.json();
      
          if (!resposta.ok || data.erro) {
            setErroCep("CEP não encontrado");
            setDadosEndereco({});
            return;
          }
      
          setDadosEndereco(data);
          setErroCep("");
      
          // Se quiser ver os dados, use um efeito ou log direto aqui
          console.log("Dados recebidos:", data);
        } catch (error) {
          setErroCep("Erro ao buscar CEP");
          setDadosEndereco({});
          console.log("Erro na requisição:", error);
        }
    };
    /////////////////////////////////////////////////////////


    /////////////validação formulário////////////////


    const validar = z.object({
        nome: z.string().min(10, {message : "Nome completo é obrigatório"}),
        dataNascimento: z.string()
            .refine(date => date.trim().length > 0, {
                message: "A data de nascimento é obrigatória"
            })
            .refine(date => new Date(date) <= new Date(), {
                message: "A data de nascimento não pode ser no futuro"
            }),
        nomeMae: z.string().min(10, {message :"Nome da Mãe é obrigatório"}),
        email: z.string()
            .email({message : "Formato de email inválido"})
            .refine(email => email.trim().length > 0, {
                message: "Email é obrigatório"
            }),
        senha: z.string()
            .min(8, "A senha deve conter no mínimo 8 digitos")
            .refine(senha => senha.trim().length > 0, {
                message: "Por favor, crie uma senha"
            }),
        confirmarSenha: z.string()
            .refine(confirmarSenha => confirmarSenha === senha, {
                message: "As senhas não coincidem"
            })
            .refine(confirmarSenha => confirmarSenha.trim().length > 0, {
                message: "Por favor confirme sua senha"
            }),
        nacionalidade: z.string()
            .refine(nacionalidade => nacionalidade.trim().length > 0, {
                message: "Por favor escolha sua nacionalidade"
            }),
        documento: z.string()
            .refine(documento => documento.trim().length > 0, {
                message: "Por favor escolha um formato de documento"
            }),
        cpf_rg: z.string()
            .refine(async (cpf_rg) => {
                const isValid = await validarCPF_RG(cpf_rg);
                return isValid;
            }, {
                message: `${documento} inválido`
            })
            .refine(cpf_rg => cpf_rg.trim().length > 0, {
                message: `Digite seu ${documento}`
            }),
        passaporte : z.string()
            .refine(passaporte => passaporte.trim().length > 0,{
                message : "Digite seu número de passaporte"
            })
            .refine((val) => /^[A-Z]{1,2}\d{6,7}$/i.test(val), {
                message: "Passaporte inválido",
              }),
        telefone : z.string()
            .refine(telefone => telefone.trim().length > 0, {
                message : "O número de telefone é obrigatório"
            }),
            // .min(10, "Telefone muito curto")
            // .refine((val) => /^\+?[0-9\s()-]{10,20}$/.test(val), {
            //   message: "Formato de telefone inválido",
            // }),
        cep : z.string()
            .refine((data) => {
                if (data.nacionalidade === "brasileiro") {
                return data.cep.trim().length > 0;
                }
                return true;
            }, {
                message: "CEP é obrigatório para brasileiros",

            }),
        logradouro : z.string()
            .refine(logradouro => logradouro.trim().length > 0,{
                message: "logradouro é obrigatório"
            }),
        numero : z.string()
            .refine(numero => numero.trim().length > 0,{
                message: "Número é obrigatório"
            }),
        bairro : z.string()
            .refine(bairro => bairro.trim().length > 0,{
                message: "Bairro é obrigatório"
            }),
        cidade : z.string()
        .refine(cidade => cidade.trim().length > 0,{
            message: "Cidade é obrigatório"
        }),
        estado : z.string()
        .refine(estado => estado.trim().length > 0,{
            message: "Estado é obrigatório"
        }),
        pais : z.string()
        .refine(pais => pais.trim().length > 0,{
            message: "País é obrigatório"
        }),

    });
    
      
    ///////////////validação de CPF//////////////////
    const validarCPF_RG = (cpf_rg) => {
    if(documento === 'CPF') {
        cpf_rg = cpf_rg.replace(/[^\d]/g, "");
        if(cpf_rg.length !== 11 || /^(\d)\1{10}$/.test(cpf_rg)) return false;
        let sum = 0;
        for (let i = 0; i < 9; i++) sum += parseInt(cpf_rg[i]) * (10 - i);
        let firstVerifier = 11 - (sum % 11);
        firstVerifier = (firstVerifier === 10 || firstVerifier === 11) ? 0 : firstVerifier;
        sum = 0;
        for (let i = 0; i < 10; i++) sum += parseInt(cpf_rg[i]) * (11 - i);
        let secondVerifier = 11 - (sum % 11);
        secondVerifier = (secondVerifier === 10 || secondVerifier === 11) ? 0 : secondVerifier;
        return cpf_rg[9] === firstVerifier.toString() && cpf_rg[10] === secondVerifier.toString();
    }
    if (documento === 'RG') {
        // Adicione a lógica de validação de RG
    }
    return true; // Caso não seja nem CPF nem RG
};
    //////////////////////////////////////////////////////////////
    
    ////////////////////////ENIO FORMULARIO///////////////////////
    const {
        register,
        handleSubmit,
        formState: { errors }
    } = useForm({
        resolver: zodResolver(validar)
    })

    const enviarFormulario = (dados) => {
        console.log("chamando a função")
        console.log(`dados ${dados}`)
        alert(`dados ${dados}`)
    }

    /////////////////////////////////////////////////////////////////

    return(
        <>
            <main>
                <HeaderComponente></HeaderComponente>
                <div className='form_content'>
                    <form onSubmit={handleSubmit(enviarFormulario)}>
                        <div className='informacoePessoais'>
                            <h5>Informações Pessoais</h5>
                            <hr></hr>
                            <div className='alinhar_content'>
                                <div className='edit_label_input'>
                                    <label className='alinhar_itens'>
                                        <User size={18}/> Nome Completo
                                    </label>
                                    <input type='text' id='nomeCompleto' placeholder='Digite seu nome completo' {...register("nome")}></input>
                                    {errors.nome && <p>{errors.nome.message}</p>}
                                </div>
                                <div className='edit_label_input'>
                                    <label className='alinhar_itens'>
                                        <Calendar size={18} />Data de Nascimento
                                    </label>
                                    <input type='date' id='dtNascimento' {...register('dataNascimento')}></input>
                                    {errors.dataNascimento && <p>{errors.dataNascimento.message}</p>}
                                </div>
                            </div>
                            <div className='alinhar_content'>
                                <div className='edit_label_input'>
                                    <label className='alinhar_itens'>
                                        <User size={18}/> Nome da Mãe
                                    </label>
                                    <input type='text' id='nomeMae' placeholder='Digite o nome da mãe completo' {...register("nomeMae")}></input>
                                    {errors.nomeMae && <p>{errors.nomeMae.message}</p>}
                                </div>
                                <div className='edit_label_input'>
                                    <label className='alinhar_itens'>
                                        <User size={18}/> Nome do Pai
                                    </label>
                                    <input type='text' id='nomePai' placeholder='Digite o nome do pai completo' {...register("nomePai")}></input>
                                </div>
                            </div>
                            <div className='alinhar_content'>
                                <div className='edit_label_input'>
                                    <label className='alinhar_itens'>
                                        <Mail size={18}/> Email
                                    </label>
                                    <input type='mail' placeholder='Digite seu email' {...register("email")}></input>
                                    {errors.email && <p>{errors.email.message}</p>}
                                </div>
                                <div className='edit_label_input'>
                                    <label className='alinhar_itens'>
                                        <Lock size={18}/> Senha
                                    </label>
                                    <input type='password' placeholder='' {...register("senha")}></input>
                                    {errors.senha && <p>{errors.senha.message}</p>}
                                </div>
                                <div className='edit_label_input'>
                                    <label className='alinhar_itens'>
                                        <Lock size={18}/> Confirmar Senha
                                    </label>
                                    <input type='password' placeholder='' {...register("confirmarSenha")}></input>
                                    {errors.confirmarSenha && <p>{errors.confirmarSenha.message}</p>}
                                </div>
                            </div>
                        </div>

                        <div className='nacionalidade_documento'>
                            <h5>Nacionalidade e Documentação</h5>
                            <hr></hr>
                            <div>
                                <label className='alinhar_itens'>
                                    <Flag size={18} /> Nacionalidade 
                                </label>
                                <div>
                                    <input type='radio'name='nacionalidade'  value='brasileiro' onChange={verificarNacionalidade} {...register("nacionalidade")}/>
                                    <label>Brasileiro</label>
                                </div>
                                <div>
                                    <input type='radio' name='nacionalidade'  value='estrangeiro' onChange={verificarNacionalidade} {...register("nacionalidade")}/>
                                    <label>Estrangeiro</label>
                                </div>
                                {errors.nacionalidade && <p>{errors.nacionalidade.message}</p>}
                            </div>
                            <div>
                                {nacionalidade === "brasileiro"  && 
                                <div>
                                    <div>
                                        <label className='alinhar_itens'>
                                            <IdCard size={18} /> Documento
                                        </label>
                                        <div>
                                            <input type='radio'name='documento' value="CPF" onChange={selecionarDocumento}  {...register("documento")}/>
                                            <label>CPF</label>
                                        </div>
                                        <div>
                                            <input type='radio' name='documento' value="RG"  onChange={selecionarDocumento}  {...register("documento")}/>
                                            <label>RG</label>
                                        </div>
                                        {errors.documento && <p>{errors.documento.message}</p>}
                                    </div>
                                    <div>
                                        <label className='alinhar_itens'>
                                            <IdCard size={18}/> Número do {documento}
                                        </label>
                                        <input type='text' placeholder={`Digite seu número de ${documento}`} onChange={(e) => validarCPF_RG(setCPF_RG(e.target.value))} {...register("cpf_rg")}></input>
                                        {errors.cpf_rg && <p>{errors.cpf_rg.message}</p>}
                                    </div>

                                </div>}
                                { nacionalidade === "estrangeiro" && 
                                    <div>
                                        <label className='alinhar_itens'>
                                        <FileText size={18}/> Número do Passaporte
                                        </label>
                                        <input type='text' placeholder='Diite seu número de passaporte' {...register("passaporte")}></input>
                                        {errors.passaporte && <p>{errors.passaporte.message}</p>}
                                    </div>
                                }
                            </div>
                        </div>

                        <div className='contato_endereco'>
                            <h5>Endereço e contato</h5>
                            <hr></hr>
                            <div className='alinhar_contato_endereco'>
                                <div>
                                    <label className='alinhar_itens'>
                                        <Phone size={18}/>Número de Telefone
                                    </label>
                                    <input type='text' placeholder='Número de telefone com DDD' {...register("telefone")}></input>
                                    {errors.telefone && <p>{errors.telefone.message}</p>}
                                </div>

                                <div>
                                    <label className='alinhar_itens'>
                                        <MapPin size={18}/>CEP
                                    </label>
                                    <input type='text' placeholder='Digite seu CEP' value={cep} onChange={(e) => setCep(e.target.value)} onBlur={buscarCep} {...register("cep")}></input>
                                    {errors.cep && <p>{errors.cep.message}</p>}
                                </div>

                                <div>
                                    <label className='alinhar_itens'>
                                        <Home size={18}/>Logradouro
                                    </label>
                                    <input type='text' placeholder='Nome da rua' value={dadosEndereco.logradouro} {...register("logradouro")}></input>
                                    {errors.logradouro && <p>{errors.logradouro.message}</p>}
                                </div>

                                <div>
                                    <label className='alinhar_itens'>
                                        <MapPinHouse size={18}/>Número
                                    </label>
                                    <input type='text' placeholder='Número' {...register("numero")}></input>
                                    {errors.numero && <p>{errors.numero.message}</p>}
                                </div>

                                <div>
                                    <label className='alinhar_itens'>
                                        <Building size={18}/>Complemento
                                    </label>
                                    <input type='text' placeholder='Apartamento, bloco, etc'></input>
                                </div>

                                <div>
                                    <label className='alinhar_itens'>
                                        <Landmark size={18}/> Bairro
                                    </label>
                                    <input type='text' placeholder='Bairro' value={dadosEndereco.bairro} {...register("bairro")}></input>
                                    {errors.bairro && <p>{errors.bairro.message}</p>}
                                </div>

                                <div>
                                    <label className='alinhar_itens'>
                                        <Building2 size={18}/>Cidade
                                    </label>
                                    <input type='text' placeholder='Cidade' value={dadosEndereco.localidade} {...register("cidade")}></input>
                                    {errors.cidade && <p>{errors.cidade.message}</p>}
                                </div>

                                <div>
                                    <label className='alinhar_itens'>
                                        <MapPin size={18}/>Estado
                                    </label>
                                    <input type='text' placeholder='Estado' value={dadosEndereco.estado} {...register("estado")}></input>
                                    {errors.estado && <p>{errors.estado.message}</p>}
                                </div>

                                <div>
                                    <label className='alinhar_itens'>
                                        <Flag size={18}/>País
                                    </label>
                                    <input type='text' placeholder='País' {...register("pais")}></input>
                                    {errors.pais && <p>{errors.pais.message}</p>}
                                </div>

                            </div>
                        </div>
                        <div>
                            <button type='submit'>ENVIAR</button>
                        </div>
                    </form>
                </div>
            </main>
        </>
    )
}

export default CadastroPage
