import "./recuperarSenha.css";
import React, { useState } from "react";
import { Link } from 'react-router-dom';

function Rodape() {
    const [popupAberto, setPopupAberto] = useState(false);

    function openPopup() {
        setPopupAberto(true);
    }

    function closePopup() {
        setPopupAberto(false);
    }

    return (
        <div className="container">
            <button type="submit" className="btn" onClick={openPopup}>
                Submit
            </button>

            <div className={`popup ${popupAberto ? "open-popup" : ""}`} id="popup">
                <h2>Hello</h2>
                <p>Lorem, ipsum dolor sit amet consectetur adipisicing elit. Hic qui voluptatum dolorem nesciunt est quae sit provident magni accusamus voluptatibus!</p>
                <button className="close-btn" onClick={closePopup}>
                    Ok
                </button>
            </div>
        </div>
    );
}

export default Rodape;
